import { format } from "date-fns";
import { Link } from "react-router-dom";

export default function Post({
  _id,
  title,
  summary,
  cover,
  content,
  createdAt,
  author,
}) {
  const tempImg =
    "https://images.unsplash.com/photo-1735437683931-b8a17f57912d?w=1000&auto=format&fit=crop&q=60&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxmZWF0dXJlZC1waG90b3MtZmVlZHwxNnx8fGVufDB8fHx8fA%3D%3D";

  return (
    <div className="post">
      <Link to={"/post/"+{_id}}>
        <div className="image">
          <img src={"http://localhost:3001/" + cover} alt="Thumbnail of post" />
        </div>
      </Link>{" "}
      <div className="content">
        <Link to={"/post/id"}>
          {" "}
          <h2>{title} </h2>
        </Link>
        <p className="info">
          <a href="/" className="author">
            {author.username}
          </a>
          <time>{format(new Date(createdAt), "yyyy-MM-dd")}</time>
        </p>
        <p className="summary">{summary}</p>
      </div>
    </div>
  );
}
